lea rdx, [rel .data]

vmovapd ymm0, [rdx]
vmovapd ymm1, [rdx + 32]

vpshufd xmm2, xmm0, 0x0
vpshufd xmm3, xmm1, 0xFF

vpshufd ymm4, ymm0, 0x0
vpshufd ymm5, ymm1, 0xFF

; Shouldn't modify vector (selector is [3, 2, 1, 0])
; Which would effectively place elements in their
; same location
