vpshufd xmm7, xmm1, 0b11100100

; [2, 0, 2, 0] shuffling
vpshufd ymm8, ymm0, 0b10001000
vpshufd xmm9, xmm1, 0b10001000

hlt

align 32
.data:
dq 0x4142434445464748
dq 0x5152535455565758
dq 0xAAAABBBBCCCCDDDD
dq 0xEEEEFFFF11112222

dq 0x6162636465666768
